import{_ as e,o as n,c as a,e as s}from"./app.3b2d76f3.js";const c={},p=s(`<h1 id="c-\u8BED\u8A00\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#c-\u8BED\u8A00\u7B80\u4ECB" aria-hidden="true">#</a> C \u8BED\u8A00\u7B80\u4ECB</h1><h2 id="\u5386\u53F2" tabindex="-1"><a class="header-anchor" href="#\u5386\u53F2" aria-hidden="true">#</a> \u5386\u53F2</h2><p>C \u8BED\u8A00\u6700\u521D\u662F\u4F5C\u4E3A Unix \u7CFB\u7EDF\u7684\u5F00\u53D1\u5DE5\u5177\u800C\u53D1\u660E\u7684\u3002</p><p>1969\u5E74\uFF0C\u7F8E\u56FD\u8D1D\u5C14\u5B9E\u9A8C\u5BA4\u7684\u80AF\xB7\u6C64\u666E\u68EE\uFF08Ken Thompson\uFF09\u4E0E\u4E39\u5C3C\u65AF\xB7\u91CC\u5947\uFF08Dennis Ritchie\uFF09\u4E00\u8D77\u5F00\u53D1\u4E86 Unix \u64CD\u4F5C\u7CFB\u7EDF\u3002Unix \u662F\u7528\u6C47\u7F16\u8BED\u8A00\u5199\u7684\uFF0C\u65E0\u6CD5\u79FB\u690D\u5230\u5176\u4ED6\u8BA1\u7B97\u673A\uFF0C\u4ED6\u4EEC\u51B3\u5B9A\u4F7F\u7528\u9AD8\u7EA7\u8BED\u8A00\u91CD\u5199\u3002\u4F46\u662F\uFF0C\u5F53\u65F6\u7684\u9AD8\u7EA7\u8BED\u8A00\u65E0\u6CD5\u6EE1\u8DB3\u4ED6\u4EEC\u7684\u8981\u6C42\uFF0C\u6C64\u666E\u68EE\u5C31\u5728 BCPL \u8BED\u8A00\u7684\u57FA\u7840\u4E0A\u53D1\u660E\u4E86 B \u8BED\u8A00\u3002</p><p>1972\u5E74\uFF0C\u4E39\u5C3C\u65AF\xB7\u91CC\u5947\u548C\u5E03\u83B1\u6069\xB7\u67EF\u6797\u6C49\uFF08Brian Kernighan\uFF09\u53C8\u5728 B \u8BED\u8A00\u7684\u57FA\u7840\u4E0A\u91CD\u65B0\u8BBE\u8BA1\u4E86\u4E00\u79CD\u65B0\u8BED\u8A00\uFF0C\u8FD9\u79CD\u65B0\u8BED\u8A00\u53D6\u4EE3\u4E86 B \u8BED\u8A00\uFF0C\u6240\u4EE5\u79F0\u4E3A C \u8BED\u8A00\u3002</p><p>1973\u5E74\uFF0C\u6574\u4E2A Unix \u7CFB\u7EDF\u90FD\u4F7F\u7528 C \u8BED\u8A00\u91CD\u5199\u3002\u6B64\u540E\uFF0C\u8FD9\u79CD\u8BED\u8A00\u5F00\u59CB\u5FEB\u901F\u6D41\u4F20\uFF0C\u5E7F\u6CDB\u7528\u4E8E\u5404\u79CD\u64CD\u4F5C\u7CFB\u7EDF\u548C\u7CFB\u7EDF\u8F6F\u4EF6\u7684\u5F00\u53D1\u3002</p><p>1988\u5E74\uFF0C\u7F8E\u56FD\u56FD\u5BB6\u6807\u51C6\u534F\u4F1A\uFF08ANSI\uFF09\u6B63\u5F0F\u5C06 C \u8BED\u8A00\u6807\u51C6\u5316\uFF0C\u6807\u5FD7\u7740 C \u8BED\u8A00\u5F00\u59CB\u7A33\u5B9A\u548C\u89C4\u8303\u5316\u3002</p><p>\u51E0\u5341\u5E74\u540E\u7684\u4ECA\u5929\uFF0CC \u8BED\u8A00\u4F9D\u7136\u662F\u6700\u5E7F\u6CDB\u4F7F\u7528\u3001\u6700\u6D41\u884C\u7684\u7CFB\u7EDF\u7F16\u7A0B\u8BED\u8A00\u4E4B\u4E00\uFF0CUnix \u548C Linux \u7CFB\u7EDF\u73B0\u5728\u8FD8\u662F\u4F7F\u7528 C \u8BED\u8A00\u5F00\u53D1\u3002</p><h2 id="c-\u8BED\u8A00\u7684\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#c-\u8BED\u8A00\u7684\u7279\u70B9" aria-hidden="true">#</a> C \u8BED\u8A00\u7684\u7279\u70B9</h2><p>C \u8BED\u8A00\u80FD\u591F\u957F\u76DB\u4E0D\u8870\u3001\u5E7F\u6CDB\u5E94\u7528\uFF0C\u4E3B\u8981\u539F\u56E0\u662F\u5B83\u6709\u4E00\u4E9B\u9C9C\u660E\u7684\u7279\u70B9\u3002</p><p>\uFF081\uFF09\u4F4E\u7EA7\u8BED\u8A00</p><p>C \u8BED\u8A00\u80FD\u591F\u76F4\u63A5\u64CD\u4F5C\u786C\u4EF6\u3001\u7BA1\u7406\u5185\u5B58\u3001\u8DDF\u64CD\u4F5C\u7CFB\u7EDF\u5BF9\u8BDD\uFF0C\u8FD9\u4F7F\u5F97\u5B83\u662F\u4E00\u79CD\u975E\u5E38\u63A5\u8FD1\u5E95\u5C42\u7684\u8BED\u8A00\uFF0C\u4E5F\u5C31\u662F\u4F4E\u7EA7\u8BED\u8A00\uFF0C\u975E\u5E38\u9002\u5408\u5199\u9700\u8981\u8DDF\u786C\u4EF6\u4EA4\u4E92\u3001\u6709\u6781\u9AD8\u6027\u80FD\u8981\u6C42\u7684\u7A0B\u5E8F\u3002</p><p>\uFF082\uFF09\u53EF\u79FB\u690D\u6027</p><p>C \u8BED\u8A00\u7684\u539F\u59CB\u8BBE\u8BA1\u76EE\u7684\uFF0C\u662F\u5C06 Unix \u7CFB\u7EDF\u79FB\u690D\u5230\u5176\u4ED6\u8BA1\u7B97\u673A\u67B6\u6784\u3002\u8FD9\u4F7F\u5F97\u5B83\u4ECE\u4E00\u5F00\u59CB\u5C31\u975E\u5E38\u6CE8\u91CD\u53EF\u79FB\u690D\u6027\uFF0CC \u7A0B\u5E8F\u53EF\u4EE5\u76F8\u5BF9\u7B80\u5355\u5730\u79FB\u690D\u5230\u5404\u79CD\u786C\u4EF6\u67B6\u6784\u548C\u64CD\u4F5C\u7CFB\u7EDF\u3002</p><p>\u9664\u4E86\u8BA1\u7B97\u673A\uFF0CC \u8BED\u8A00\u73B0\u5728\u8FD8\u662F\u5D4C\u5165\u5F0F\u7CFB\u7EDF\u7684\u9996\u9009\u7F16\u7A0B\u8BED\u8A00\uFF0C\u6C7D\u8F66\u3001\u7167\u76F8\u673A\u3001\u5BB6\u7528\u7535\u5668\u7B49\u8BBE\u5907\u7684\u5E95\u5C42\u7CFB\u7EDF\u90FD\u662F\u7528 C \u8BED\u8A00\u7F16\u7A0B\uFF0C\u8FD9\u4E5F\u662F\u56E0\u4E3A\u5B83\u826F\u597D\u7684\u53EF\u79FB\u690D\u6027\u3002</p><p>\uFF083\uFF09\u7B80\u5355\u6027</p><p>C \u8BED\u8A00\u7684\u8BED\u6CD5\u76F8\u5BF9\u7B80\u5355\uFF0C\u8BED\u6CD5\u89C4\u5219\u4E0D\u7B97\u592A\u591A\uFF0C\u4E5F\u51E0\u4E4E\u6CA1\u6709\u8BED\u6CD5\u7CD6\u3002\u4E00\u822C\u6765\u8BF4\uFF0C\u5982\u679C\u4E24\u4E2A\u8BED\u6CD5\u53EF\u4EE5\u5B8C\u6210\u51E0\u4E4E\u76F8\u540C\u7684\u4E8B\u60C5\uFF0CC \u8BED\u8A00\u5C31\u53EA\u4F1A\u63D0\u4F9B\u4E00\u79CD\uFF0C\u8FD9\u6837\u5927\u5927\u51CF\u5C11\u4E86\u8BED\u8A00\u7684\u590D\u6742\u6027\u3002</p><p>\u800C\u4E14\uFF0CC \u8BED\u8A00\u7684\u8BED\u6CD5\u90FD\u662F\u57FA\u7840\u8BED\u6CD5\uFF0C\u4E0D\u63D0\u4F9B\u9AD8\u7EA7\u7684\u6570\u636E\u7ED3\u6784\uFF0C\u6BD4\u5982 C \u8BED\u8A00\u6CA1\u6709\u201C\u7C7B\u201D\uFF08class\uFF09\uFF0C\u590D\u6742\u7684\u6570\u636E\u7ED3\u6784\u90FD\u9700\u8981\u81EA\u5DF1\u6784\u9020\u3002</p><p>\uFF084\uFF09\u7075\u6D3B\u6027</p><p>C \u8BED\u8A00\u5BF9\u7A0B\u5E8F\u5458\u7684\u9650\u5236\u5F88\u5C11\u3002\u5B83\u5047\u8BBE\u7A0B\u5E8F\u5458\u77E5\u9053\u81EA\u5DF1\u5728\u5E72\u561B\uFF0C\u4E0D\u4F1A\u9650\u5236\u4F60\u505A\u5404\u79CD\u5371\u9669\u7684\u64CD\u4F5C\uFF0C\u4F60\u5E72\u4EC0\u4E48\u90FD\u53EF\u4EE5\uFF0C\u540E\u679C\u4E5F\u7531\u81EA\u5DF1\u8D1F\u8D23\u3002</p><p>C \u8BED\u8A00\u7684\u54F2\u5B66\u662F\u201C\u4FE1\u4EFB\u7A0B\u5E8F\u5458\uFF0C\u4E0D\u8981\u59A8\u788D\u4ED6\u4EEC\u505A\u4E8B\u201D\u3002\u6BD4\u5982\uFF0C\u5B83\u8BA9\u7A0B\u5E8F\u5458\u81EA\u5DF1\u7BA1\u7406\u5185\u5B58\uFF0C\u4E0D\u63D0\u4F9B\u5185\u5B58\u81EA\u52A8\u6E05\u7406\u529F\u80FD\u3002\u53E6\u5916\uFF0C\u4E5F\u4E0D\u63D0\u4F9B\u7C7B\u578B\u68C0\u67E5\u3001\u6570\u7EC4\u7684\u8D1F\u7D22\u5F15\u68C0\u67E5\u3001\u6307\u9488\u4F4D\u7F6E\u7684\u68C0\u67E5\u7B49\u4FDD\u62A4\u63AA\u65BD\u3002</p><p>\u8868\u9762\u4E0A\u770B\uFF0C\u8FD9\u4F3C\u4E4E\u5F88\u5371\u9669\uFF0C\u4F46\u662F\u5BF9\u4E8E\u9AD8\u7EA7\u7A0B\u5E8F\u5458\u6765\u8BF4\uFF0C\u5374\u6709\u4E86\u66F4\u5927\u7684\u7F16\u7A0B\u81EA\u7531\u3002\u4E0D\u8FC7\uFF0C\u8FD9\u4E5F\u4F7F\u5F97 C \u8BED\u8A00\u7684 debug \u4E0D\u592A\u5BB9\u6613\u3002</p><p>\uFF085\uFF09\u603B\u7ED3</p><p>\u4E0A\u9762\u8FD9\u4E9B\u7279\u70B9\uFF0C\u4F7F\u5F97 C \u8BED\u8A00\u53EF\u4EE5\u5199\u51FA\u6027\u80FD\u975E\u5E38\u5F3A\u3001\u5B8C\u5168\u53D1\u6325\u786C\u4EF6\u6F5C\u529B\u7684\u7A0B\u5E8F\uFF0C\u800C\u4E14 C \u8BED\u8A00\u7684\u7F16\u8BD1\u5668\u5B9E\u73B0\u96BE\u5EA6\u76F8\u5BF9\u8F83\u4F4E\u3002\u4F46\u662F\u53E6\u4E00\u65B9\u9762\uFF0CC \u8BED\u8A00\u4EE3\u7801\u5BB9\u6613\u51FA\u9519\uFF0C\u4E00\u822C\u7A0B\u5E8F\u5458\u4E0D\u5BB9\u6613\u5199\u597D\u3002</p><p>\u6B64\u5916\uFF0C\u5F53\u4EE3\u5F88\u591A\u6D41\u884C\u8BED\u8A00\u90FD\u662F\u4EE5 C \u8BED\u8A00\u4E3A\u57FA\u7840\uFF0C\u6BD4\u5982 C++\u3001Java\u3001C#\u3001JavaScript \u7B49\u7B49\u3002\u5B66\u597D C \u8BED\u8A00\u6709\u52A9\u4E8E\u5BF9\u8FD9\u4E9B\u8BED\u8A00\u52A0\u6DF1\u7406\u89E3\u3002</p><h2 id="c-\u8BED\u8A00\u7684\u7248\u672C" tabindex="-1"><a class="header-anchor" href="#c-\u8BED\u8A00\u7684\u7248\u672C" aria-hidden="true">#</a> C \u8BED\u8A00\u7684\u7248\u672C</h2><p>\u5386\u53F2\u4E0A\uFF0CC \u8BED\u8A00\u6709\u8FC7\u591A\u4E2A\u7248\u672C\u3002</p><p>\uFF081\uFF09K\uFF06R C</p><p><code>K&amp;R C</code>\u6307\u7684\u662F C \u8BED\u8A00\u7684\u539F\u59CB\u7248\u672C\u30021978\u5E74\uFF0CC \u8BED\u8A00\u7684\u53D1\u660E\u8005\u4E39\u5C3C\u65AF\xB7\u91CC\u5947\uFF08Dennis Ritchie\uFF09\u548C\u5E03\u83B1\u6069\xB7\u67EF\u6797\u6C49\uFF08Brian Kernighan\uFF09\u5408\u5199\u4E86\u4E00\u672C\u8457\u540D\u7684\u6559\u6750\u300AC \u7F16\u7A0B\u8BED\u8A00\u300B\uFF08The C programming language\uFF09\u3002\u7531\u4E8E C \u8BED\u8A00\u8FD8\u6CA1\u6709\u6210\u6587\u7684\u8BED\u6CD5\u6807\u51C6\uFF0C\u8FD9\u672C\u4E66\u5C31\u6210\u4E86\u516C\u8BA4\u6807\u51C6\uFF0C\u4EE5\u4E24\u4F4D\u4F5C\u8005\u7684\u59D3\u6C0F\u9996\u5B57\u6BCD\u4F5C\u4E3A\u7248\u672C\u7B80\u79F0\u201CK&amp;R C\u201D\u3002</p><p>\uFF082\uFF09ANSI C\uFF08\u53C8\u79F0 C89 \u6216 C90\uFF09</p><p>C \u8BED\u8A00\u7684\u539F\u59CB\u7248\u672C\u975E\u5E38\u7B80\u5355\uFF0C\u5BF9\u5F88\u591A\u60C5\u51B5\u7684\u63CF\u8FF0\u975E\u5E38\u6A21\u7CCA\uFF0C\u52A0\u4E0A C \u8BED\u6CD5\u4F9D\u7136\u5728\u5FEB\u901F\u53D1\u5C55\uFF0C\u8981\u6C42\u5C06 C \u8BED\u8A00\u6807\u51C6\u5316\u7684\u547C\u58F0\u8D8A\u6765\u8D8A\u9AD8\u3002</p><p>1989\u5E74\uFF0C\u7F8E\u56FD\u56FD\u5BB6\u6807\u51C6\u534F\u4F1A\uFF08ANSI\uFF09\u5236\u5B9A\u4E86\u4E00\u5957 C \u8BED\u8A00\u6807\u51C6\u30021990\u5E74\uFF0C\u56FD\u9645\u6807\u51C6\u5316\u7EC4\u7EC7\uFF08ISO\uFF09\u901A\u8FC7\u4E86\u8FD9\u4E2A\u6807\u51C6\u3002\u5B83\u88AB\u79F0\u4E3A\u201CANSI C\u201D\uFF0C\u4E5F\u53EF\u4EE5\u6309\u7167\u53D1\u5E03\u5E74\u4EFD\uFF0C\u79F0\u4E3A\u201CC89 \u6216 C90\u201D\u3002</p><p>\uFF083\uFF09C95</p><p>1995\u5E74\uFF0C\u7F8E\u56FD\u56FD\u5BB6\u6807\u51C6\u534F\u4F1A\u5BF91989\u5E74\u7684\u90A3\u4E2A\u6807\u51C6\uFF0C\u8FDB\u884C\u4E86\u8865\u5145\uFF0C\u52A0\u5165\u591A\u5B57\u8282\u5B57\u7B26\u548C\u5BBD\u5B57\u7B26\u7684\u652F\u6301\u3002\u8FD9\u4E2A\u7248\u672C\u79F0\u4E3A C95\u3002</p><p>\uFF084\uFF09C99</p><p>C \u8BED\u8A00\u6807\u51C6\u7684\u7B2C\u4E00\u6B21\u5927\u578B\u4FEE\u8BA2\uFF0C\u53D1\u751F\u57281999\u5E74\uFF0C\u589E\u52A0\u4E86\u8BB8\u591A\u8BED\u8A00\u7279\u6027\uFF0C\u6BD4\u5982\u53CC\u659C\u6760\uFF08<code>//</code>\uFF09\u7684\u6CE8\u91CA\u8BED\u6CD5\u3002\u8FD9\u4E2A\u7248\u672C\u79F0\u4E3A C99\uFF0C\u662F\u76EE\u524D\u6700\u6D41\u884C\u7684 C \u7248\u672C\u3002</p><p>\uFF085\uFF09C11</p><p>2011\u5E74\uFF0C\u6807\u51C6\u5316\u7EC4\u7EC7\u518D\u4E00\u6B21\u5BF9 C \u8BED\u8A00\u8FDB\u884C\u4FEE\u8BA2\uFF0C\u589E\u52A0\u4E86 Unicode \u548C\u591A\u7EBF\u7A0B\u7684\u652F\u6301\u3002\u8FD9\u4E2A\u7248\u672C\u79F0\u4E3A C11\u3002</p><p>\uFF086\uFF09C17</p><p>C11 \u6807\u51C6\u57282017\u5E74\u8FDB\u884C\u4E86\u4FEE\u8865\uFF0C\u4F46\u53D1\u5E03\u662F\u57282018\u5E74\u3002\u65B0\u7248\u672C\u53EA\u662F\u89E3\u51B3\u4E86 C11 \u7684\u4E00\u4E9B\u7F3A\u9677\uFF0C\u6CA1\u6709\u5F15\u5165\u4EFB\u4F55\u65B0\u529F\u80FD\u3002\u8FD9\u4E2A\u7248\u672C\u79F0\u4E3A C17\u3002</p><p>\uFF087\uFF09C2x</p><p>\u6807\u51C6\u5316\u7EC4\u7EC7\u6B63\u5728\u8BA8\u8BBA C \u8BED\u8A00\u7684\u4E0B\u4E00\u4E2A\u7248\u672C\uFF0C\u636E\u8BF4\u53EF\u80FD\u4F1A\u57282023\u5E74\u901A\u8FC7\uFF0C\u5230\u65F6\u5C31\u4F1A\u79F0\u4E3A C23\u3002</p><h2 id="c-\u8BED\u8A00\u7684\u7F16\u8BD1" tabindex="-1"><a class="header-anchor" href="#c-\u8BED\u8A00\u7684\u7F16\u8BD1" aria-hidden="true">#</a> C \u8BED\u8A00\u7684\u7F16\u8BD1</h2><p>C \u8BED\u8A00\u662F\u4E00\u79CD\u7F16\u8BD1\u578B\u8BED\u8A00\uFF0C\u6E90\u7801\u90FD\u662F\u6587\u672C\u6587\u4EF6\uFF0C\u672C\u8EAB\u65E0\u6CD5\u6267\u884C\u3002\u5FC5\u987B\u901A\u8FC7\u7F16\u8BD1\u5668\uFF0C\u751F\u6210\u4E8C\u8FDB\u5236\u7684\u53EF\u6267\u884C\u6587\u4EF6\uFF0C\u624D\u80FD\u6267\u884C\u3002\u7F16\u8BD1\u5668\u5C06\u4EE3\u7801\u4ECE\u6587\u672C\u7FFB\u8BD1\u6210\u4E8C\u8FDB\u5236\u6307\u4EE4\u7684\u8FC7\u7A0B\uFF0C\u5C31\u79F0\u4E3A\u7F16\u8BD1\u9636\u6BB5\uFF0C\u53C8\u79F0\u4E3A\u201C\u7F16\u8BD1\u65F6\u201D\uFF08compile time\uFF09\uFF0C\u8DDF\u8FD0\u884C\u9636\u6BB5\uFF08\u53C8\u79F0\u4E3A\u201C\u8FD0\u884C\u65F6\u201D\uFF09\u76F8\u533A\u5206\u3002</p><p>\u76EE\u524D\uFF0C\u6700\u5E38\u89C1\u7684 C \u8BED\u8A00\u7F16\u8BD1\u5668\u662F\u81EA\u7531\u8F6F\u4EF6\u57FA\u91D1\u4F1A\u63A8\u51FA\u7684 GCC \u7F16\u8BD1\u5668\uFF0C\u5B83\u53EF\u4EE5\u514D\u8D39\u4F7F\u7528\u3002\u672C\u4E66\u4E5F\u4F7F\u7528\u8FD9\u4E2A\u7F16\u8BD1\u5668\u3002Linux \u548C Mac \u7CFB\u7EDF\u53EF\u4EE5\u76F4\u63A5\u5B89\u88C5 GCC\uFF0CWindows \u7CFB\u7EDF\u53EF\u4EE5\u5B89\u88C5 MinGW\u3002\u4F46\u662F\uFF0C\u4E5F\u53EF\u4EE5\u4E0D\u7528\u8FD9\u4E48\u9EBB\u70E6\uFF0C\u7F51\u4E0A\u6709\u5728\u7EBF\u7F16\u8BD1\u5668\uFF0C\u80FD\u591F\u76F4\u63A5\u5728\u7F51\u9875\u4E0A\u6A21\u62DF\u8FD0\u884C C \u4EE3\u7801\uFF0C\u67E5\u770B\u7ED3\u679C\uFF0C\u4E0B\u9762\u5C31\u662F\u4E24\u4E2A\u8FD9\u6837\u7684\u5DE5\u5177\u3002</p><ul><li>CodingGround: https://tutorialspoint.com/compile_c_online.php</li><li>OnlineGDB: https://onlinegdb.com/online_c_compiler</li></ul><p>\u672C\u4E66\u7684\u4F8B\u5B50\u90FD\u4F7F\u7528 GCC \u5728\u547D\u4EE4\u884C\u8FDB\u884C\u7F16\u8BD1\u3002</p><h2 id="hello-world-\u793A\u4F8B" tabindex="-1"><a class="header-anchor" href="#hello-world-\u793A\u4F8B" aria-hidden="true">#</a> Hello World \u793A\u4F8B</h2><p>C \u8BED\u8A00\u7684\u6E90\u4EE3\u7801\u6587\u4EF6\uFF0C\u901A\u5E38\u4EE5\u540E\u7F00\u540D<code>.c</code>\u7ED3\u5C3E\u3002\u4E0B\u9762\u662F\u4E00\u4E2A\u7B80\u5355\u7684 C \u7A0B\u5E8F<code>hello.c</code>\u3002\u5B83\u5C31\u662F\u4E00\u4E2A\u666E\u901A\u7684\u6587\u672C\u6587\u4EF6\uFF0C\u4EFB\u4F55\u6587\u672C\u7F16\u8BD1\u5668\u90FD\u80FD\u7528\u6765\u5199\u3002</p><div class="language-c ext-c line-numbers-mode"><pre class="language-c"><code><span class="token macro property"><span class="token directive-hash">#</span><span class="token directive keyword">include</span> <span class="token string">&lt;stdio.h&gt;</span></span>

<span class="token keyword">int</span> <span class="token function">main</span><span class="token punctuation">(</span><span class="token keyword">void</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token function">printf</span><span class="token punctuation">(</span><span class="token string">&quot;Hello World\\n&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  <span class="token keyword">return</span> <span class="token number">0</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\u4E0A\u9762\u8FD9\u4E2A\u7A0B\u5E8F\u7684\u552F\u4E00\u4F5C\u7528\uFF0C\u5C31\u662F\u5728\u5C4F\u5E55\u4E0A\u9762\u663E\u793A\u201CHello World\u201D\u3002</p><p>\u8FD9\u91CC\u4E0D\u8BB2\u89E3\u8FD9\u4E9B\u4EE3\u7801\u662F\u4EC0\u4E48\u610F\u601D\uFF0C\u53EA\u662F\u4F5C\u4E3A\u4E00\u4E2A\u4F8B\u5B50\uFF0C\u8BA9\u5927\u5BB6\u770B\u770B C \u4EE3\u7801\u5E94\u8BE5\u600E\u4E48\u7F16\u8BD1\u548C\u8FD0\u884C\u3002\u5047\u8BBE\u4F60\u5DF2\u7ECF\u5B89\u88C5\u597D\u4E86 GCC \u7F16\u8BD1\u5668\uFF0C\u53EF\u4EE5\u6253\u5F00\u547D\u4EE4\u884C\uFF0C\u6267\u884C\u4E0B\u9762\u7684\u547D\u4EE4\u3002</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ gcc hello.c
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>\u4E0A\u9762\u547D\u4EE4\u4F7F\u7528<code>gcc</code>\u7F16\u8BD1\u5668\uFF0C\u5C06\u6E90\u6587\u4EF6<code>hello.c</code>\u7F16\u8BD1\u6210\u4E8C\u8FDB\u5236\u4EE3\u7801\u3002\u6CE8\u610F\uFF0C<code>$</code>\u662F\u547D\u4EE4\u884C\u63D0\u793A\u7B26\uFF0C\u4F60\u771F\u6B63\u9700\u8981\u8F93\u5165\u7684\u662F<code>$</code>\u540E\u9762\u7684\u90E8\u5206\u3002</p><p>\u8FD0\u884C\u8FD9\u4E2A\u547D\u4EE4\u4EE5\u540E\uFF0C\u9ED8\u8BA4\u4F1A\u5728\u5F53\u524D\u76EE\u5F55\u4E0B\u751F\u6210\u4E00\u4E2A\u7F16\u8BD1\u4EA7\u7269\u6587\u4EF6<code>a.out</code>\uFF08assembler output \u7684\u7F29\u5199\uFF0CWindows \u5E73\u53F0\u4E3A<code>a.exe</code>\uFF09\u3002\u6267\u884C\u8BE5\u6587\u4EF6\uFF0C\u5C31\u4F1A\u5728\u5C4F\u5E55\u4E0A\u8F93\u51FA<code>Hello World</code>\u3002</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ ./a.out
Hello World
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><p>GCC \u7684<code>-o</code>\u53C2\u6570\uFF08output \u7684\u7F29\u5199\uFF09\u53EF\u4EE5\u6307\u5B9A\u7F16\u8BD1\u4EA7\u7269\u7684\u6587\u4EF6\u540D\u3002</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ gcc <span class="token parameter variable">-o</span> hello hello.c
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>\u4E0A\u9762\u547D\u4EE4\u7684<code>-o hello</code>\u6307\u5B9A\uFF0C\u7F16\u8BD1\u4EA7\u7269\u7684\u6587\u4EF6\u540D\u4E3A<code>hello</code>\uFF08\u53D6\u4EE3\u9ED8\u8BA4\u7684<code>a.out</code>\uFF09\u3002\u7F16\u8BD1\u540E\u5C31\u4F1A\u751F\u6210\u4E00\u4E2A\u540D\u53EB<code>hello</code>\u7684\u53EF\u6267\u884C\u6587\u4EF6\uFF0C\u76F8\u5F53\u4E8E\u4E3A<code>a.out</code>\u6307\u5B9A\u4E86\u540D\u79F0\u3002\u6267\u884C\u8BE5\u6587\u4EF6\uFF0C\u4E5F\u4F1A\u5F97\u5230\u540C\u6837\u7684\u7ED3\u679C\u3002</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ ./hello
Hello World
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><p>GCC \u7684<code>-std=</code>\u53C2\u6570\uFF08standard \u7684\u7F29\u5199\uFF09\u8FD8\u53EF\u4EE5\u6307\u5B9A\u6309\u7167\u54EA\u4E2A C \u8BED\u8A00\u7684\u6807\u51C6\u8FDB\u884C\u7F16\u8BD1\u3002</p><div class="language-bash ext-sh line-numbers-mode"><pre class="language-bash"><code>$ gcc <span class="token parameter variable">-std</span><span class="token operator">=</span>c99 hello.c
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>\u4E0A\u9762\u547D\u4EE4\u6307\u5B9A\u6309\u7167 C99 \u6807\u51C6\u8FDB\u884C\u7F16\u8BD1\u3002</p><p>\u6CE8\u610F\uFF0C<code>-std</code>\u540E\u9762\u9700\u8981\u7528<code>=</code>\u8FDE\u63A5\u53C2\u6570\uFF0C\u800C\u4E0D\u662F\u50CF\u4E0A\u9762\u7684<code>-o</code>\u4E00\u6837\u7528\u7A7A\u683C\uFF0C\u5E76\u4E14<code>=</code>\u524D\u540E\u4E5F\u4E0D\u80FD\u6709\u591A\u4F59\u7684\u7A7A\u683C\u3002</p>`,64),d=[p];function i(o,l){return n(),a("div",null,d)}const t=e(c,[["render",i],["__file","intro.html.vue"]]);export{t as default};
